select.if:
select id,title,date_pub from articles

      and DATE_FORMAT(date_pub, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{date}, '%Y-%m-%d %H:%T:%s')   

order by date_pub desc;

select id,title,date_pub from articles

      and DATE_FORMAT(date_pub, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{date}, '%Y-%m-%d %H:%T:%s')   

order by date_pub desc limit 0,#{num};
